import { useContext, useEffect } from 'react';
import { GameContext } from '../context/GameContext';
import { tiles } from '../data/tiles';

const GameController = () => {
  const {
    players,
    setPlayers,
    currentPlayerIndex,
    originalCount,
    isMoving,
    setOriginalCount,
    setIsMoving
  } = useContext(GameContext);

  useEffect(() => {
    if (!isMoving || originalCount <= 0) return;

    const moveLoop = async () => {
      let steps = originalCount;
      const updated = [...players];
      const me = updated[currentPlayerIndex];

      while (steps > 0) {
        const current = tiles.find(t => t.id === me.tileId);
        if (!current?.next?.length) break;

        // filter out backtrack
        let opts = current.next.filter(o => o.from !== me.prevTile);
        if (!opts.length) opts = current.next;

        let nextOption;
        if (opts.length === 1) {
          nextOption = opts[0];
        } else if (originalCount === 7) {
          nextOption = opts[0];
        } else {
          const rollType = originalCount < 7 ? 'below' : 'above';
          nextOption = opts.find(o => o.roll === rollType) || opts[0];
        }

        if (!nextOption || typeof nextOption.to !== 'number') break;

        me.prevTile = me.tileId;
        me.tileId = nextOption.to;
        setPlayers([...updated]);
        steps--;
        await new Promise(r => setTimeout(r, 400));
      }

      setOriginalCount(0);
      setIsMoving(false);
    };

    moveLoop();
  }, [
    isMoving,
    originalCount,
    players,
    currentPlayerIndex,
    setPlayers,
    setOriginalCount,
    setIsMoving
  ]);

  return null;
};

export default GameController;
