import React, { useContext } from 'react';
import { GameContext } from '../context/GameContext';
import Board from './Board';
import DiceRoller from './DiceRoller';
import Dashboard from './Dashboard';
import PlayerStats from './PlayerStats';

const GameScreen = () => {
  const { players, player, currentPlayerIndex } = useContext(GameContext);
  const current = players[currentPlayerIndex];

  return (
    <div style={{ position: 'relative', height: '100vh', overflow: 'hidden' }}>
      <Board />

      {/* Only show dice UI for the active player */}
      <DiceRoller />

      {/* Other players’ mini‐dashboards */}
      <div style={{
        position: 'absolute',
        top: 10,
        left: 10,
        display: 'flex',
        flexDirection: 'column',
        gap: '10px'
      }}>
        {players.filter(p => p.name !== player?.name).map(p => (
          <PlayerStats key={p.name} player={p} />
        ))}
      </div>

      {/* Bottom‐center main dashboard */}
      <div style={{
        position: 'absolute',
        bottom: 0,
        left: 0,
        right: 0
      }}>
        <Dashboard />
      </div>
    </div>
  );
};

export default GameScreen;
