import React, { useContext, useEffect } from 'react';
import { GameContext } from '../context/GameContext';

const tiles = [
  { id: 1, name: 'Start' },
  { id: 2, name: 'Yonex' },
  { id: 3, name: 'Li-Ning' },
  { id: 4, name: 'Stone Paper Scissors' },
  { id: 5, name: 'Victor' },
  { id: 6, name: 'Teleport' },
  { id: 7, name: 'Adidas' },
  { id: 8, name: 'Casino' },
  { id: 9, name: 'Road Cash' },
  { id: 10, name: 'Nike' },
  { id: 11, name: 'Stone Paper Scissors' },
  { id: 12, name: 'Under Armour' },
  { id: 13, name: 'Teleport' },
  { id: 14, name: 'Puma' },
  { id: 15, name: 'Casino' },
  { id: 16, name: 'Road Cash' },
  { id: 17, name: 'Fila' },
  { id: 18, name: 'Stone Paper Scissors' },
  { id: 19, name: 'New Balance' },
  { id: 20, name: 'Teleport' },
  { id: 21, name: 'Reebok' },
  { id: 22, name: 'Casino' },
  { id: 23, name: 'Road Cash' },
  { id: 24, name: 'Asics' },
  { id: 25, name: 'Stone Paper Scissors' },
  { id: 26, name: 'Mizuno' },
  { id: 27, name: 'Teleport' },
  { id: 28, name: 'Converse' },
  { id: 29, name: 'Casino' },
  { id: 30, name: 'Road Cash' }
];

export default function Dashboard() {
  const { player } = useContext(GameContext);

  useEffect(() => {
    console.log('[Dashboard] Player money updated:', {
      playerName: player?.name,
      money: player?.money
    });
  }, [player?.money]);

  if (!player) return null;

  const currentTile = tiles.find(t => t.id === player.tileId) || { name: 'Unknown' };

  return (
    <div style={{
      display: 'flex',
      justifyContent: 'center',
      marginTop: '1.5rem'
    }}>
      <div style={{
        border: '2px solid #444',
        borderRadius: 10,
        padding: '1.5rem 2rem',
        width: '80%',
        maxWidth: 700,
        backgroundColor: '#fff',
        boxShadow: '0 2px 6px rgba(99, 11, 11, 0.1)',
        textAlign: 'center'
      }}>
        <h3 style={{ fontSize: '1.8rem', marginBottom: '1rem' }}>{player.name}'s Dashboard</h3>
        <div style={{ 
          color: '#4CAF50', 
          marginBottom: '10px',
          fontSize: '2rem',
          fontWeight: 'bold'
        }}>
          Money: ${player?.money || 0}
        </div>
        <div style={{ 
          color: '#f44336', 
          marginBottom: '10px',
          fontSize: '2rem',
          fontWeight: 'bold'
        }}>
          Loan: ${player?.loan || 0}
        </div>
        <div style={{ 
          color: '#2196F3', 
          marginBottom: '10px',
          fontSize: '2rem',
          fontWeight: 'bold'
        }}>
          At: {currentTile.name}
        </div>
      </div>
    </div>
  );
}
